mapper.insert:
INSERT INTO address_book (
    user_id, consignee, phone, sex,
    province_code, province_name, city_code, city_name,
    district_code, district_name, detail, label, is_default
) VALUES (
    #{userId}, #{consignee}, #{phone}, #{sex},
    #{provinceCode}, #{provinceName}, #{cityCode}, #{cityName},
    #{districtCode}, #{districtName}, #{detail}, #{label}, #{isDefault}
)

mapper.update:
UPDATE address_book
consignee = #{consignee}, phone = #{phone}, sex = #{sex}, province_code = #{provinceCode}, province_name = #{provinceName}, city_code = #{cityCode}, city_name = #{cityName}, district_code = #{districtCode}, district_name = #{districtName}, detail = #{detail}, label = #{label},
WHERE id = #{id} AND user_id = #{userId}
