mapper.select:
select * from orders


        and user_id = #{userId}


        and status = #{status}


        and number like concat('%', #{number}, '%')


        and phone like concat('%', #{phone}, '%')


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(id) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

mapper.update:
update orders
set status = #{toStatus}
where id = #{id}
  and status = #{fromStatus}

update orders
set status = 5,
    delivery_time = #{deliveryTime}
where id = #{id}
  and status = 4

update orders
set status = 6,
    rejection_reason = #{rejectionReason},
    cancel_time = #{cancelTime}
where id = #{id}
  and status = 2

update orders
set status = 6,
    cancel_reason = #{cancelReason},
    cancel_time = #{cancelTime}
where id = #{id}
  and status = #{fromStatus}

update orders
set status = 6,
    cancel_reason = #{cancelReason},
    cancel_time = #{cancelTime}
where id = #{id}
  and status NOT IN (5, 6)

mapper.select:
select DATE(order_time) as date, sum(amount) as turnover
from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}


group by DATE(order_time)
order by date

select DATE(order_time) as date, count(id) as count
from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}


group by DATE(order_time)
order by date
