mapper.select:
select s.id, s.name, s.image, c.name as categoryName, s.price, s.status, s.update_time
    from setmeal s left join category c on s.category_id = c.id

    s.is_deleted = 0

        and s.name like concat('%',#{name},'%')


        and s.category_id = #{categoryId}


        and s.status = #{status}

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

update setmeal

    status = #{status},
    update_time = #{updateTime},
    update_user = #{updateUser}

where id = #{id}

update setmeal set is_deleted = 1, delete_time = #{deleteTime}, update_time = #{deleteTime}
id in
#{id}
1 = 0
